import React from 'react'
import Card from './Card'


function DoneImg(props) {
    if (props.done) {
        return (<img src="./images/s.png" alt='checked'></img>)
    } else {
        return (<img src="./images/n.png" alt='unchecked'></img>)
    }
}

function List(props) {
    return (<ul  >
        {props.items.map(item => <li className={item.done ? "done item" : "item"} key={item.id}>
            <Card>
                {item.text}
                <div>
                    <button onClick={() => { props.onDone(item) }}><DoneImg done={item.done}></DoneImg></button>
                    <button className="deleteButton" onClick={() => { props.onItemDeleted(item) }}><img src="./images/trashcan.PNG" alt='delete'></img></button>
                </div>
            </Card>
        </li>)}
    </ul>)
}

export default List;
